SELECT bfh.id,bfh.md5,bfh.file_size,bfh.backup_file_id,bfh.modify_time FROM backup_file_history bfh INNER JOIN ( SELECT backup_file_id, MAX(backup_end_time) AS max_backup_end_time FROM backup_file_history WHERE backup_file_id IN
#{fileId}
GROUP BY backup_file_id ) max_dates ON bfh.backup_file_id = max_dates.backup_file_id AND bfh.backup_end_time = max_dates.max_backup_end_time
